import Day06.findStartOfMessage
import Day06.input

fun main() {

    val answer1 = findStartOfMessage(input, 4)
    println(answer1) // 1702

    val answer2 = findStartOfMessage(input, 14)
    println(answer2) // 3559
}

object Day06 {

    class RingBuffer(private val capacity: Int) {
        private val buffer = CharArray(capacity)
        private var index = 0

        fun write(char: Char) {
            buffer[index++ % capacity] = char
        }

        fun allDifferent(): Boolean = buffer.filter { it != '\u0000'}.distinct().size == capacity
    }

    fun findStartOfMessage(data: String, distinctThreshold: Int): Int {
        val buf = RingBuffer(distinctThreshold)
        var index = 0
        data.takeWhile {
            buf.write(it).also { index++ }
            !buf.allDifferent()
        }
        return index
    }

    const val input = ""
}